    private void poly_CBXFocusLost(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_poly_CBXFocusLost
 Object a = poly_CBX.getSelectedItem();
        String q= a.toString();
        if (q.equals("-----")){
            poly_lbl.setText("0");
        } else {
            double nprs= Double.parseDouble(q);
            double amt= nprs*rt.bpoly;
            poly_lbl.setText(""+amt);
        }
        polylbl1=l1.getText();
        polycbx=poly_CBX.getSelectedItem();
        polylbl2=poly_lbl.getText();
        // TODO add your handling code here:
    }//GEN-LAST:event_poly_CBXFocusLost

