    private void polyItemStateChanged(java.awt.event.ItemEvent evt) {//GEN-FIRST:event_polyItemStateChanged
     if (evt.getStateChange()==1){
    l1.setVisible(true);
    poly_CBX.setVisible(true);
    poly_lbl.setVisible(true);
}
else if (evt.getStateChange()==2){
 l1.setVisible(false);
    poly_CBX.setVisible(false);
    poly_lbl.setVisible(false);

}            // TODO add your handling code here:
}//GEN-LAST:event_polyItemStateChanged

